  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {};
export const metadata = {
  "id": "js/adva/promises",
  "title": "promises",
  "description": "# Promises in Js",
  "source": "@site/docs\\js\\adva\\promises.md",
  "permalink": "/docs/js/adva/promises"
};
