  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'js_freecode',
	title: 'FreecodeCamp Js notes'
};
export const metadata = {
  "id": "js/js_freecode",
  "title": "FreecodeCamp Js notes",
  "description": "# FreeCodeCamp.org ",
  "source": "@site/docs\\js\\JS.md",
  "permalink": "/docs/js/js_freecode"
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "freecodecamporg"
    }}>{`FreeCodeCamp.org`}</h1>
    <p><a parentName="p" {...{
        "href": "https://www.youtube.com/watch?v=PkZNo7MFNFg&list=PLTFW0aa-32LYNEOEMsrycVfEBn8FQPrBW&index=15&t=0s"
      }}>{`https://www.youtube.com/watch?v=PkZNo7MFNFg&list=PLTFW0aa-32LYNEOEMsrycVfEBn8FQPrBW&index=15&t=0s`}</a></p>
    <p>{`These are notes taken from the video. Only various subjected have noted.`}</p>
    <h1 {...{
      "id": "comments"
    }}>{`Comments`}</h1>
    <p>{`Single line comments are done as `}<inlineCode parentName="p">{`// This is a comment`}</inlineCode>{` This can be done anywhere`}</p>
    <p>{`Multi Line comments are done as `}<inlineCode parentName="p">{`/* text */`}</inlineCode></p>
    <h1 {...{
      "id": "data-types"
    }}>{`Data Types`}</h1>
    <p>{`Undefined - something that hasnt been defined`}</p>
    <p>{`null - not assigned to anything`}</p>
    <p>{`boolean - True or False`}</p>
    <p>{`String - Text`}</p>
    <p>{`symbol - unique value`}</p>
    <p>{`number - is a number `}</p>
    <p>{`object - can store values`}</p>
    <h1 {...{
      "id": "variables"
    }}>{`Variables`}</h1>
    <p>{`Something to fill your data in. `}</p>
    <p>{`Three types of variable.`}</p>
    <p>{`var / let / const`}</p>
    <p><inlineCode parentName="p">{`var myname = "jack"`}</inlineCode>{` and the same with let. let and var can be changed`}</p>
    <p><inlineCode parentName="p">{`const pi = 3.14`}</inlineCode>{` This stands for constant, it can never be altered. `}</p>
    <h1 {...{
      "id": "storing-values-with-an-operator"
    }}>{`Storing Values with an operator`}</h1>
    <p><inlineCode parentName="p">{`var a;`}</inlineCode>{` delcaring a variable called a. Can be assigned later if needed.`}</p>
    <p><inlineCode parentName="p">{`var b = 2;`}</inlineCode>{` delcaring a variable called b and assigning it a value of 2`}</p>
    <p><inlineCode parentName="p">{`a = 7;`}</inlineCode></p>
    <p><inlineCode parentName="p">{`b = a;`}</inlineCode>{` This assigns the content of a to the variable b (b would = 7)`}</p>
    <h1 {...{
      "id": "variable-tips"
    }}>{`Variable tips`}</h1>
    <p>{`Text assigned to a variable is a string. This needs to be in speechmarks "". `}<inlineCode parentName="p">{`let myName = "jack";`}</inlineCode></p>
    <p>{`A number is different. `}<inlineCode parentName="p">{`let myAge = 19;`}</inlineCode>{` No speech marks needed`}</p>
    <h1 {...{
      "id": "consolelog"
    }}>{`Console.log`}</h1>
    <p><inlineCode parentName="p">{`console.log()`}</inlineCode>{` - This allows you to log data into the dev console.`}</p>
    <p>{`such as `}<inlineCode parentName="p">{`console.log(b)`}</inlineCode>{` // Would print 7`}</p>
    <h1 {...{
      "id": "math-in-js"
    }}>{`Math in Js`}</h1>
    <p>{`Addition /`}</p>
    <pre><code parentName="pre" {...{}}>{`let sum = 10 + 10;
console.log(sum); // will print 20
`}</code></pre>
    <p>{`Subraction `}</p>
    <p><inlineCode parentName="p">{`let sum = 10 - 1; // print 9`}</inlineCode></p>
    <p>{`Multiplication`}</p>
    <p><inlineCode parentName="p">{`let sum = 10 * 10;`}</inlineCode>{` // 100`}</p>
    <p>{`Divide `}</p>
    <p>{`Is done using / `}</p>
    <p>{`Incrementing a number`}</p>
    <p><inlineCode parentName="p">{` var sum = 100; sum++;`}</inlineCode>{` equals 101`}</p>
    <p>{`decementing a number`}</p>
    <p>{`This is done the same but with --`}</p>
    <h1 {...{
      "id": "appending-varibales-to-strings"
    }}>{`Appending Varibales to Strings`}</h1>
    <pre><code parentName="pre" {...{}}>{`var anAdjective = "awesome";
var ourStr = "code is ";
ourStr += anAdjective; // Prints code is awesome
`}</code></pre>
    <h1 {...{
      "id": "finding-the-length-of-a-string"
    }}>{`Finding the length of a string`}</h1>
    <pre><code parentName="pre" {...{}}>{`let firstname = "jack";

firstnamelegnth = firstname.length; // is 4
console.log(firstnamelength) // prints 4
`}</code></pre>
    <h1 {...{
      "id": "finding-the-first-character-of-a-string"
    }}>{`Finding the first character of a string`}</h1>
    <pre><code parentName="pre" {...{}}>{`let name = "jack";

firstletter = firstName[0]; // equal to J as js starts with 1 as 0


`}</code></pre>
    <h1 {...{
      "id": "storing-multiple-values-with-arrays"
    }}>{`Storing multiple values with Arrays`}</h1>
    <pre><code parentName="pre" {...{}}>{`var ourArray = ["jack", 23]; Elements are seperated with a comma;
`}</code></pre>
    <h1 {...{
      "id": "nested-arrays"
    }}>{`Nested Arrays`}</h1>
    <pre><code parentName="pre" {...{}}>{`var OurArray = [["astley", 40], ["yo", 17]];
`}</code></pre>
    <h1 {...{
      "id": "acess-array-data-with-index-brackets"
    }}>{`Acess array data with index brackets`}</h1>
    <pre><code parentName="pre" {...{}}>{`var ourArray = [50, 60, 70];
var ourData = ourArray[0]; // Equal to 50;
`}</code></pre>
    <h1 {...{
      "id": "modify-arrays-with-index-brackets"
    }}>{`Modify arrays with index brackets`}</h1>
    <pre><code parentName="pre" {...{}}>{`var ourArray = [50, 60, 70];
ourArray[1] = 45; This replaces 60 with 45 in the array; 
`}</code></pre>
    <h1 {...{
      "id": "manipulating-arrays-with-push"
    }}>{`Manipulating Arrays with .push()`}</h1>
    <p>{`Appending data to the end of an Array`}</p>
    <pre><code parentName="pre" {...{}}>{`var ourArray = [1, 2, 3];
ourArray.push([4]); // Pushes 4 to the end of the array
`}</code></pre>
    <h1 {...{
      "id": "manipulating-arrays-with-pop"
    }}>{`Manipulating Arrays with .pop()`}</h1>
    <p>{`Removes an element from our Array`}</p>
    <pre><code parentName="pre" {...{}}>{`let array = [1, 2, 3];
let removeArray = array.pop(); // This removes the last element from the array and assigns it to the variable removeArray;
`}</code></pre>
    <h1 {...{
      "id": "manipulating-arrays-with-shift"
    }}>{`Manipulating Arrays with .shift()`}</h1>
    <p>{`This removes the first element in an Array instead of the last like .pop()`}</p>
    <pre><code parentName="pre" {...{}}>{`let array = [1, 2];
let removeArray = array.shift(); array now equals 2 but removeArray variable equals to 1;

`}</code></pre>
    <h1 {...{
      "id": "manipulating-arrays-with-unshift"
    }}>{`Manipulating Arrays with .unshift`}</h1>
    <p>{`Similiar to push. This adds to the start of an array instead of the end`}</p>
    <pre><code parentName="pre" {...{}}>{`let array = [1, 2, 3];
array.unshift(0); // adds 0 to the start of the array
`}</code></pre>
    <h1 {...{
      "id": "javascript-objects"
    }}>{`Javascript objects`}</h1>
    <p>{`Similar to arrays yet they use properties instead of indexes. \\
As with variables a string is in brackets and a number isnt. Also each property is seperated with a comma.`}</p>
    <pre><code parentName="pre" {...{}}>{`let dog = {
    "name": "jack",
    "legs": 4,
    "tails": 1
};
`}</code></pre>
    <h1 {...{
      "id": "acessing-an-object-property-in-different-ways"
    }}>{`Acessing an object property in different ways;`}</h1>
    <pre><code parentName="pre" {...{}}>{`let clothes = {
    "hat": "red",
    "shirt": "slack",
    "shoes": "sliders"
};

let hatColor = clothes.hat; // hatcolor = red
let shoeType = clothes.shoe; // shoeType = sliders

console.log(hatColor); // Prints red
console.log(shoeType); // Prints sliders
`}</code></pre>
    <p>{`This can also be done by using bracket notation instead of .notation`}</p>
    <p>{`A bracket notation has to be used if the property has a space.`}</p>
    <p><inlineCode parentName="p">{` var hatColor = clothes["hat"];`}</inlineCode></p>
    <h1 {...{
      "id": "acessing-an-object-with-a-variable"
    }}>{`Acessing an object with a variable`}</h1>
    <pre><code parentName="pre" {...{}}>{`let nameId = {
    1: "jack",
    2: "bec"
};

let nameNumber = 1; equals nameNumber to 1 
let name = nameId[nameNumber]; equals name to nameNumber
`}</code></pre>
    <h1 {...{
      "id": "updating-objective-properties"
    }}>{`Updating objective Properties`}</h1>
    <pre><code parentName="pre" {...{}}>{`let name {
    "name": "jack",
    "job": "sales"
}

name.job = "coder"; //updates job to coder;
`}</code></pre>
    <h1 {...{
      "id": "delete-a-object-property"
    }}>{`Delete a object property`}</h1>
    <p><inlineCode parentName="p">{`delete name.job; // This deletes the job property from the name object`}</inlineCode></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
