  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'props',
	title: 'Props in react'
};
export const metadata = {
  "id": "Reactjs/props",
  "title": "Props in react",
  "description": "## Access a Component's props",
  "source": "@site/docs/Reactjs/props.md",
  "permalink": "/docs/Reactjs/props",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Components in React.JS()",
    "permalink": "/docs/Reactjs/components"
  },
