  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {};
export const metadata = {
  "id": "js/adva/modules",
  "title": "modules",
  "description": "# modules",
  "source": "@site/docs/js/adva/modules.md",
  "permalink": "/docs/js/adva/modules"
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Modules in Javascript',
  id: 'modules-in-javascript',
  children: []
}, {
  value: 'Export',
  id: 'export',
  children: []
}, {
  value: 'require()',
  id: 'require',
  children: []
}, {
  value: 'Logging / calling an import',
  id: 'logging--calling-an-import',
  children: []
}, {
  value: 'Export default',
  id: 'export-default',
  children: []
}, {
  value: 'Import',
  id: 'import',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "modules"
    }}>{`modules`}</h1>
    <h2 {...{
      "id": "modules-in-javascript"
    }}>{`Modules in Javascript`}</h2>
    <p>{`We can get started with modules by defining a module in one file and making the module available for use in another file.`}</p>
    <h2 {...{
      "id": "export"
    }}>{`Export`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`let Menu = {};
Menu.specialty = "Roasted Beet Burger with Mint Sauce";

module.exports = Menu;
`}</code></pre>
    <h2 {...{
      "id": "require"
    }}>{`require`}{`(`}{`)`}</h2>
    <p>{`To make use of the exported module and the behavior we define within it, we import the module. A common way to do this is with the require`}{`(`}{`)`}{` function.`}</p>
    <p><inlineCode parentName="p">{`const Menu = require('./menu.js');`}</inlineCode></p>
    <p>{`We can also wrap any collection of data and functions in an object, and export the object using module.exports`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`module.exports = {
  specialty: "Roasted Beet Burger with Mint Sauce",
  getSpecialty: function() {
    return this.specialty;
  } 
};
`}</code></pre>
    <h2 {...{
      "id": "logging--calling-an-import"
    }}>{`Logging / calling an import`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`const Airplane = require('./2-airplane.js');

console.log(Airplane.displayAirplane());
`}</code></pre>
    <h2 {...{
      "id": "export-default"
    }}>{`Export default`}</h2>
    <p>{`As of ES6, JavaScript has implemented a new more readable and flexible syntax for exporting modules. These are usually broken down into one of two techniques, default export and named exports.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`let Menu = {}; 

export default Menu;
`}</code></pre>
    <h2 {...{
      "id": "import"
    }}>{`Import`}</h2>
    <p>{`ES6 module syntax also introduces the import keyword for importing objects.`}</p>
    <p><inlineCode parentName="p">{`import Menu from './menu';`}</inlineCode></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
