  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'components',
	title: 'Components in React.JS()'
};
export const metadata = {
  "id": "Reactjs/components",
  "title": "Components in React.JS()",
  "description": "React applications are made out of components.\r",
  "source": "@site/docs\\Reactjs\\component.md",
  "permalink": "/docs/Reactjs/components",
  "sidebar": "someSidebar",
  "previous": {
    "title": "JSX in react",
    "permalink": "/docs/Reactjs/jsx"
  }
};
