export const rightToc = [{
  value: 'Always make sure you import react',
  id: 'always-make-sure-you-import-react',
  children: []
}, {
  value: 'In your main page always import reactDom',
  id: 'in-your-main-page-always-import-reactdom',
  children: []
}, {
  value: 'Creating a component class',
  id: 'creating-a-component-class',
  children: []
