  value: 'Create a Component Instance',
  id: 'create-a-component-instance',
  children: []
}, {
  value: 'Interacting Components',
  id: 'interacting-components',
  children: []
}, {
  value: 'Importing &amp; Exporting a file',
  id: 'importing--exporting-a-file',
  children: [{
    value: 'import',
    id: 'import',
