  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {};
export const metadata = {
  "id": "css/adva/flexbox",
  "title": "flexbox",
  "description": "# FlexBox",
  "source": "@site/docs/css/adva/flexbox.md",
  "permalink": "/docs/css/adva/flexbox"
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'FlexBox',
  id: 'flexbox-1',
  children: []
}, {
  value: 'Exampe of setting up a flexbox container',
  id: 'exampe-of-setting-up-a-flexbox-container',
  children: []
}, {
  value: 'Justify Content (FlexBox Positing)',
  id: 'justify-content-flexbox-positing',
  children: []
}, {
  value: 'Align Items',
  id: 'align-items',
  children: []
}, {
  value: 'Flex-Grow (Grows the css in porportion to the screen size)',
  id: 'flex-grow-grows-the-css-in-porportion-to-the-screen-size',
  children: []
}, {
  value: 'Flex-Shrink',
  id: 'flex-shrink',
  children: []
}, {
  value: 'Flex-basis',
  id: 'flex-basis',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "flexbox"
    }}>{`FlexBox`}</h1>
    <h2 {...{
      "id": "flexbox-1"
    }}>{`FlexBox`}</h2>
    <p>{`To designate an element as a flex container, set the element’s display property to flex or inline-flex.`}</p>
    <p>{`justify-content align-items flex-grow flex-shrink flex-basis flex flex-wrap align-content flex-direction flex-flow\\`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`div.container {
  display: flex;
}
`}</code></pre>
    <p>{`If you don't want div elements to be block-level elements, we would use display: inline. Flexbox, however, provides the inline-flex value for the display attribute, which allows us to create flex containers that are also inline elements.`}</p>
    <h2 {...{
      "id": "exampe-of-setting-up-a-flexbox-container"
    }}>{`Exampe of setting up a flexbox container`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`<div class="container">
  <p>I’m also a flex item!</p>
  <p>Me too!</p>
</div>
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`.container {
  width: 200px;
  height: 200px;
  display: inline-flex;
}
`}</code></pre>
    <h2 {...{
      "id": "justify-content-flexbox-positing"
    }}>{`Justify Content `}{`(`}{`FlexBox Positing`}{`)`}</h2>
    <p>{`To position the items from left to right, we use a property called justify-content  flex-start — all items will be positioned in order starting, from the left of the parent container, with no extra space between or before them. flex-end — all items will be positioned in order, with the last item starting on the right side of the parent container, with no extra space between or after them. center — all items will be positioned in order, in the center of the parent container with no extra space before, between, or after them. space-around — items will be positioned with equal space before and after each item, resulting in double the space between elements. space-between — items will be positioned with equal space between them, but no extra space before the first or after the last elements.`}</p>
    <h2 {...{
      "id": "align-items"
    }}>{`Align Items`}</h2>
    <p>{`The align-items property makes it possible to space flex items vertically.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`.container {
  align-items: baseline;
}
`}</code></pre>
    <p>{`In the example, the align-items property is set to baseline. This means that the baseline of the content of each item will be aligned.`}</p>
    <p>{`There are five values we can use for the align-items property:`}</p>
    <p>{`flex-start — all elements will be positioned at the top of the parent container.`}</p>
    <p>{`flex-end — all elements will be positioned at the bottom of the parent container.`}</p>
    <p>{`center — the center of all elements will be positioned halfway between the top and bottom of the parent container.`}</p>
    <p>{`baseline — the bottom of the content of all items will be aligned with each other.`}</p>
    <p>{`stretch — if possible, the items will stretch from top to bottom of the container `}{`(`}{`this is the default value; elements with a specified height will not stretch; elements with a minimum height or no height specified will stretch`}{`)`}{`.`}</p>
    <h2 {...{
      "id": "flex-grow-grows-the-css-in-porportion-to-the-screen-size"
    }}>{`Flex-Grow `}{`(`}{`Grows the css in porportion to the screen size`}{`)`}</h2>
    <p>{`The flex-grow property allows us to specify if items should grow to fill a container and also which items should grow proportionally more or less than others.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`<div class="container">
  <div class="side">
    <h1>I’m on the side of the flex container!</h1>
  </div>
  <div class="center">
    <h1>I'm in the center of the flex container!</h1>
  </div>
  <div class=”side”>
    <h1>I'm on the other side of the flex container!</h1>
  </div>
</div>
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`.container {
  display: flex;
}

.side {
  width: 100px;
  flex-grow: 1;
}

.center {
  width: 100px;
  flex-grow: 2;
}
`}</code></pre>
    <h2 {...{
      "id": "flex-shrink"
    }}>{`Flex-Shrink`}</h2>
    <p>{`Just as the flex-grow property proportionally stretches flex items, the flex-shrink property can be used to specify which elements will shrink and in what proportions.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`.container {
  display: flex;
}

.side {
  width: 100px;
  flex-shrink: 1;
}

.center {
  width: 100px;
  flex-shrink: 2;
}
`}</code></pre>
    <h2 {...{
      "id": "flex-basis"
    }}>{`Flex-basis`}</h2>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
