  import { mdx } from '@mdx-js/react';

  export const frontMatter = {};
export const metadata = {
  "id": "js/tech/iterators",
  "title": "iterators",
  "description": "# iterators",
  "source": "@site/docs\\js\\tech\\iterators.md",
  "permalink": "/docs/js/tech/iterators"
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Iterators in Js',
  id: 'iterators-in-js',
  children: []
}, {
  value: 'Functions as Data',
  id: 'functions-as-data',
  children: []
}, {
  value: '.forEach Method',
  id: 'foreach-method',
  children: []
}, {
  value: '.map() method',
  id: 'map-method',
  children: []
}, {
  value: '.filter Method',
  id: 'filter-method',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "iterators"
    }}>{`iterators`}</h1>
    <h2 {...{
      "id": "iterators-in-js"
    }}>{`Iterators in Js`}</h2>
    <h2 {...{
      "id": "functions-as-data"
    }}>{`Functions as Data`}</h2>
    <p>{`JavaScript functions behave like any other data type in the language; we can assign functions to variables, and we can reassign them to new variables.`}</p>
    <p>{`We can get really long functions such as`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`const announceThatIAmDoingImportantWork = () => {
    console.log("I’m doing very important work!");
};
`}</code></pre>
    <p>{`And re asign that function to a shorter varibale`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`const busy = announceThatIAmDoingImportantWork;

busy(); // This function call barely takes any space!
`}</code></pre>
    <h2 {...{
      "id": "foreach-method"
    }}>{`.forEach Method`}</h2>
    <p>{`This method will execute the same code for each element of an array.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`groceries.forEach(groceryItem => console.log(groceryItem));
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`const fruits = ['mango', 'papaya', 'pineapple', 'apple'];

// Iterate over fruits below
fruits.forEach(paf => console.log('I want to eat a '+ paf));
`}</code></pre>
    <p>{`The code above will log a nicely formatted list of the fruits to the console.`}</p>
    <h2 {...{
      "id": "map-method"
    }}>{`.map`}{`(`}{`)`}{` method`}</h2>
    <p>{`When .map`}{`(`}{`)`}{` is called on an array, it takes an argument of a callback function and returns a new array`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`const numbers = [1, 2, 3, 4, 5]; 

const bigNumbers = numbers.map(number => {
  return number * 10;
});
`}</code></pre>
    <p>{`In the above example: numbers is an array. bigNumbers will store the return of .map on numbers. numbers.map will iterate through the numbers array. It will save each value from the numbers array, times by 10, into a new array.  Eample:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`console.log(numbers); // Output: [1, 2, 3, 4, 5]
console.log(bigNumbers); // Output: [10, 20, 30, 40, 50]
`}</code></pre>
    <h2 {...{
      "id": "filter-method"
    }}>{`.filter Method`}</h2>
    <p>{`This method also returns a new array. However, returns an array of elements after filtering out certain elements from the original array. The callback function for the .filter`}{`(`}{`)`}{` method should return true or false depending on the element that is passed to it. The elements that cause the callback function to return true are added to the new array.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`const words = ['chair', 'music', 'pillow', 'brick', 'pen', 'door']; 

const shortWords = words.filter(word => {
  return word.length < 6;
});
`}</code></pre>
    </MDXLayout>;
}

;
